import React, { useState } from 'react';
import './App.css';
import Person from './Person/Person';

const App = (props) => {
  const [personsState, setPersonsState] = useState({
    persons: [
      { name: 'Morr', visit: 22 },
      { name: 'Rath', visit: 11 },
      { name: 'Nerg', visit: 44 },
    ],
  });

  return (
    <div className='App'>
      <h1>Hi, I'm a React App</h1>
      <button onClick={this.nameSwitchHandler}>Switch Button</button>
      <Person
        name={personsState.persons[0].name}
        visit={personsState.persons[0].visit}
      />
      <Person
        name={personsState.persons[1].name}
        visit={personsState.persons[1].visit}
      />
      <Person
        name={personsState.persons[2].name}
        visit={personsState.persons[2].visit}>
        Color: Black
      </Person>
    </div>
  );
};

export default App;

