import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
      { name: 'Dante', visit: 3 },
      { name: 'Chun', visit: 5 },
      { name: 'Felyne', visit: 7 },
    ],
  };

  nameSwitchHandler = () => {
    //console.log('clicked');
    this.setState({
      persons: [
        { name: 'Dante', visit: 3 },
        { name: 'Chun', visit: 5 },
        { name: 'Felyne', visit: 7 },
      ],
    });
  };
  render() {
    return (
      <div className='App'>
        <h1>Hi, I'm a React App</h1>
        <button onClick={this.nameSwitchHandler}>Switch Button</button>
        <Person
          name={this.state.persons[0].name}
          visit={this.state.persons[0].visit}
        />
        <Person
          name={this.state.persons[1].name}
          visit={this.state.persons[1].visit}
        />
        <Person
          name={this.state.persons[2].name}
          visit={this.state.persons[2].visit}>
          Color: Black
        </Person>
      </div>
    );
  }
}

export default App;
